                "confidence": 0.90,
                "buy_rate": 0.70,
                "click_rate": 0.75
            },
            "houndstooth": {
                "confidence": 0.98,
                "buy_rate": 0.85,
                "click_rate": 0.90
            }
        },
        "style_tags": {
            "preppy": {
                "confidence": 0.90,
                "buy_rate": 0.80,
                "click_rate": 0.85
            },
            "classic": {
                "confidence": 0.95,
                "buy_rate": 0.85,
                "click_rate": 0.90
            }
        },
        "usage_tags": {
            "winter": {
                "confidence": 0.98,
                "buy_rate": 0.90,
